(function() {

    module.exports.sendResponse = async(ctx, result) => {
        result = (typeof result == "undefined") ? {} : result;
        let response = (typeof result.status != "number") ? { "status": 501, "responseData": { "errorCode": "ERR501", "message": "No response return found"}} : result;
        let successObj = response;

        ctx.status = successObj.status;
        ctx.body = successObj;
        return ctx;
    }
})();