module.exports = async (ctx, next) => {
    let serverBlocker = ctx.header["x-auth-server"] || false;
    if (!serverBlocker) {
        let response = {"status": 401, "responseData": { "errorCode": "ERR401", "message": "Direct Access restricted. You can use configured domain to access this service"}};
        ctx.status = response.status;
        ctx.body = response;
    } else {
        await next();
    }
};